/****** Script for SelectTopNRows command from SSMS  ******/
CREATE VIEW [dbo].[WP_OML_COMPLIANCE_INDEX_CALCULATIONS]
AS

SELECT I.CompanyName, I.Year_of_WP, I.Consession_Type, I.Unscaled_Score, I.Unscaled_Score_sum, I.Scaled_by_Reciprocal_GrandTotal_RGT, I.MAX_RGT, I.MIN_RGT, I.Recalibrated_Scaled_Index, J.Weight, I.Recalibrated_Scaled_Index * J.Weight AS Weighted_Score, I.INDEX_TYPE FROM (SELECT CompanyName, Year_of_WP, Consession_Type, Unscaled_Score, Unscaled_Score_sum, Scaled_by_Reciprocal_GrandTotal_RGT, MAX_RGT, MIN_RGT, MIN_RGT + ISNULL((Scaled_by_Reciprocal_GrandTotal_RGT - MIN_RGT) / NULLIF ((MAX_RGT - MIN_RGT) * (100 - 0), 0), 0) AS Recalibrated_Scaled_Index, 'Compliance Index' AS INDEX_TYPE FROM (SELECT F.CompanyName, F.Year_of_WP, F.Consession_Type, F.Unscaled_Score, F.Unscaled_Score_sum, F.Scaled_by_Reciprocal_GrandTotal_RGT, G.MAX_RGT, G.MIN_RGT FROM (SELECT D.CompanyName, D.Year_of_WP, D.Consession_Type, D.Unscaled_Score, E.Unscaled_Score_sum, ISNULL(D.Unscaled_Score / NULLIF (CONVERT(decimal(18, 1), E.Unscaled_Score_sum), 0) * 100, 0) AS Scaled_by_Reciprocal_GrandTotal_RGT FROM (SELECT DISTINCT CompanyName, Year_of_WP, Consession_Type, MAX(Total_Sum_All_Division) AS Unscaled_Score FROM ( SELECT CompanyName ,Consession_Type ,Year_of_WP ,Total_Sum_All_Division FROM dbo.WP_OML_COMPLIANCE_INDEX_CALCULATIONS WHERE Consession_Type ='OML') AS A GROUP BY CompanyName, Year_of_WP, Consession_Type) AS D INNER JOIN (SELECT Year_of_WP, SUM(Unscaled_Score) AS Unscaled_Score_sum FROM (SELECT DISTINCT CompanyName, Year_of_WP, Consession_Type, MAX(Total_Sum_All_Division) AS Unscaled_Score FROM (
SELECT CompanyName ,Consession_Type ,Year_of_WP ,Total_Sum_All_Division FROM dbo.WP_OML_COMPLIANCE_INDEX_CALCULATIONS WHERE Consession_Type ='OML'
) AS A GROUP BY CompanyName, Year_of_WP, Consession_Type) AS c GROUP BY Year_of_WP) AS E ON D.Year_of_WP = E.Year_of_WP) AS F INNER JOIN dbo.WP_OPL_Discovery_Index_MN_MAX_RGT_by_YEAR AS G ON F.Year_of_WP = G.Year_of_WP) AS H) AS I INNER JOIN (SELECT INDEX_TYPE, Weight, CONCESSIONTYPE FROM dbo.ADMIN_PERFOMANCE_INDEX WHERE (CONCESSIONTYPE = 'OML') AND (INDEX_TYPE = 'Compliance Index')) AS J ON I.INDEX_TYPE = J.INDEX_TYPE

/****** Script for SelectTopNRows command from SSMS  ******/
CREATE VIEW [dbo].[WP_OML_INCREMENT_IN_PRODUCTION_STAGE_1]
AS

SELECT I.CompanyName, I.Year_of_WP, I.Consession_Type, I.Unscaled_Score, I.Unscaled_Score_sum, I.Scaled_by_Reciprocal_GrandTotal_RGT, I.MAX_RGT, I.MIN_RGT, I.Recalibrated_Scaled_Index, J.Weight, I.Recalibrated_Scaled_Index * J.Weight AS Weighted_Score, I.INDEX_TYPE FROM (SELECT CompanyName, Year_of_WP, Consession_Type, Unscaled_Score, Unscaled_Score_sum, Scaled_by_Reciprocal_GrandTotal_RGT, MAX_RGT, MIN_RGT, MIN_RGT + ISNULL((Scaled_by_Reciprocal_GrandTotal_RGT - MIN_RGT) / NULLIF ((MAX_RGT - MIN_RGT) * (100 - 0), 0), 0) AS Recalibrated_Scaled_Index, 'Reserve Replacement Ratio' AS INDEX_TYPE FROM (SELECT F.CompanyName, F.Year_of_WP, F.Consession_Type, F.Unscaled_Score, F.Unscaled_Score_sum, F.Scaled_by_Reciprocal_GrandTotal_RGT, G.MAX_RGT, G.MIN_RGT FROM (SELECT D.CompanyName, D.Year_of_WP, D.Consession_Type, D.Unscaled_Score, E.Unscaled_Score_sum, ISNULL(D.Unscaled_Score / NULLIF (CONVERT(decimal(18, 1), E.Unscaled_Score_sum), 0) * 100, 0) AS Scaled_by_Reciprocal_GrandTotal_RGT FROM (SELECT DISTINCT CompanyName, Year_of_WP, Consession_Type, MAX(Unscaled_Score) AS Unscaled_Score FROM (
SELECT CompanyName ,Year_of_WP ,Consession_Type ,Reserves_as_at_MMbbl ,Reserves_as_at_MMbbl_P1 ,Total_Production_ ,Unscaled_Score FROM dbo.WP_OML_RESERVE_REPLACEMENT_RATIO_STAGE_1
) AS A GROUP BY CompanyName, Year_of_WP, Consession_Type) AS D INNER JOIN (SELECT Year_of_WP, SUM(Unscaled_Score) AS Unscaled_Score_sum FROM (SELECT DISTINCT CompanyName, Year_of_WP, Consession_Type, MAX(Unscaled_Score) AS Unscaled_Score FROM (
SELECT CompanyName ,Year_of_WP ,Consession_Type ,Reserves_as_at_MMbbl ,Reserves_as_at_MMbbl_P1 ,Total_Production_ ,Unscaled_Score FROM dbo.WP_OML_RESERVE_REPLACEMENT_RATIO_STAGE_1
) AS A GROUP BY CompanyName, Year_of_WP, Consession_Type) AS c GROUP BY Year_of_WP) AS E ON D.Year_of_WP = E.Year_of_WP) AS F INNER JOIN dbo.WP_OML_Reserve_Replacement_Ratio_INDEX_MN_MAX_RGT_by_YEAR AS G ON F.Year_of_WP = G.Year_of_WP) AS H) AS I INNER JOIN (SELECT INDEX_TYPE, Weight, CONCESSIONTYPE FROM dbo.ADMIN_PERFOMANCE_INDEX WHERE (CONCESSIONTYPE = 'OML') AND (INDEX_TYPE = 'Reserve Replacement Ratio')) AS J ON I.INDEX_TYPE = J.INDEX_TYPE

SELECT Year_of_WP, MAX(Scaled_by_Reciprocal_GrandTotal_RGT) AS MAX_RGT, MIN(Scaled_by_Reciprocal_GrandTotal_RGT) AS MIN_RGT FROM (SELECT b.CompanyName, b.Year_of_WP, b.Utilized, b.Produced, b.Unscaled_Score, d.Unscaled_Score_sum, b.Unscaled_Score / CONVERT(decimal(18, 1), d.Unscaled_Score_sum) * 100 AS Scaled_by_Reciprocal_GrandTotal_RGT FROM (SELECT CompanyName, Year_of_WP, Utilized, Produced, Utilized / CONVERT(decimal(18, 1), Produced) * 100 AS Unscaled_Score FROM (SELECT CompanyName, Year_of_WP, SUM(CAST(total_no_of_nigeria_senior_staff AS int)) AS Utilized, SUM(CAST(total_no_of_senior_staff AS int)) AS Produced FROM dbo.NIGERIA_CONTENT_QUESTION GROUP BY CompanyName, Year_of_WP) AS a) AS b INNER JOIN (SELECT Year_of_WP, SUM(Unscaled_Score) AS Unscaled_Score_sum FROM (SELECT CompanyName, Year_of_WP, Utilized, Produced, Utilized / CONVERT(decimal(18, 1), Produced) * 100 AS Unscaled_Score FROM (
SELECT CompanyName, Year_of_WP, SUM(CAST(total_no_of_nigeria_senior_staff AS int)) AS Utilized, SUM(CAST(total_no_of_senior_staff AS int)) AS Produced , Consession_Type FROM dbo.NIGERIA_CONTENT_QUESTION AS NIGERIA_CONTENT_QUESTION_1 WHERE Consession_Type = 'OML' GROUP BY CompanyName, Year_of_WP , Consession_Type
) AS a_1) AS c GROUP BY Year_of_WP) AS d ON b.Year_of_WP = d.Year_of_WP) AS f GROUP BY Year_of_WP

SELECT Year_of_WP, MAX(Scaled_by_Reciprocal_GrandTotal_RGT) AS MAX_RGT, MIN(Scaled_by_Reciprocal_GrandTotal_RGT) AS MIN_RGT FROM (SELECT b.CompanyName, b.Year_of_WP, b.Utilized, b.Produced, b.Unscaled_Score, d.Unscaled_Score_sum, b.Unscaled_Score / CONVERT(decimal(18, 1), d.Unscaled_Score_sum) * 100 AS Scaled_by_Reciprocal_GrandTotal_RGT FROM (SELECT CompanyName, Year_of_WP, Utilized, Produced, Utilized / CONVERT(decimal(18, 1), Produced) * 100 AS Unscaled_Score FROM (SELECT CompanyName, Year_of_WP, SUM(CAST(total_no_of_top_nigerian_management_staff AS int)) AS Utilized, SUM(CAST(total_no_of_top_management_staff AS int)) AS Produced FROM dbo.NIGERIA_CONTENT_QUESTION GROUP BY CompanyName, Year_of_WP) AS a) AS b INNER JOIN (SELECT Year_of_WP, SUM(Unscaled_Score) AS Unscaled_Score_sum FROM (SELECT CompanyName, Year_of_WP, Utilized, Produced, Utilized / CONVERT(decimal(18, 1), Produced) * 100 AS Unscaled_Score FROM (
SELECT CompanyName, Year_of_WP, SUM(CAST(total_no_of_top_nigerian_management_staff AS int)) AS Utilized, SUM(CAST(total_no_of_top_management_staff AS int)) AS Produced , Consession_Type FROM dbo.NIGERIA_CONTENT_QUESTION AS NIGERIA_CONTENT_QUESTION_1 WHERE Consession_Type = 'OML' GROUP BY CompanyName, Year_of_WP , Consession_Type
) AS a_1) AS c GROUP BY Year_of_WP) AS d ON b.Year_of_WP = d.Year_of_WP) AS f GROUP BY Year_of_WP

